equations.eq:4:
\begin{equation}
\label{eq:policy-gradient}
\begin{aligned}
\triangledown_\theta J(\pi_\theta) = \int_{s \in \mathcal{S}} \rho(s) \sum_{a \in \mathcal{A}} \triangledown_\theta \pi_\theta(a|s) Q(s,a) ds \\
= E_{s \sim \rho, a \in \pi_\theta} [\triangledown_\theta \log \pi_\theta(a|s) Q(s,a)]
\end{aligned}
\end{equation}

equations.eq:6:
\begin{equation}
\label{eq:long-term-reward-loss}
\begin{aligned}
J(w_l, b_l) = E_{s_t \sim \rho} [ R^l(s_t, a_t) - z_t)^2] \\
z_t = R^m(s_t,a_t) + \gamma R^l(s_{t+1}, \hat{a}_{t+1}),
\end{aligned}
\end{equation}

equations.eq:8:
\begin{equation}
\label{eq:mortality-risk-prediction-loss}
J(w_m, b_m) = E_{s_t \sim \rho} [-y\log (p_m(s_t, a_t)) - (1-y) \log (1 - p_m(s_t, a_t))]
\end{equation}

equations.eq:9:
\begin{equation}
\label{eq:short-reward}
R^s(s_t, a_t) = \sum_{a \in \mathcal{A}} \pi_\theta(a|s_t) p_m(s_t, a) - p_m(s_t, a_t)
\end{equation}

equations.eq:12:
\begin{equation}
\label{eq:MLE}
\begin{aligned}
J(w_d, b_d) = E_{s_t \sim \rho} [ f^T(s_t, a_t) - s_{t+1})^2]
\end{aligned}
\end{equation}
