% \begin{align}
% 	\textsf{PFFN}(\bm{H}^{(l)}) &= [\textsf{FFN}(\bm{h}_1^{(l)})^\trans, \cdots, \textsf{FFN}(\bm{h}_t^{(l)})^\trans] \\
% 	\textsf{FFN}(\bm{x}) &= \textsf{GELU}(\bm{x}\bm{W}_1^{(l)} + \bm{b}_1^{(l)})\bm{W}_2^{(l)}+\bm{b}_2^{(l)},
% \end{align}
% where $\bm{W}_1^{(l)} \in \mathbb{R}^{d \times d_h}, \bm{W}_2^{(l)} \in \mathbb{R}^{d_h \times d}, \bm{b}_1^{(l)} \in \mathbb{R}^{d_h}, \bm{b}_2^{(l)} \in \mathbb{R}^{d}$ are all learnable parameters. $d_h$ is the dimensional size of the hidden states in the \textsf{PFFN} to strengthen the expression ability of the network, which is usually set as $4d$.
